/**
 * Make sure that there is only one top-level heading in the document by
 * adjusting headings depths accordingly.
 *
 * @template {Node} T
 * @param {T} tree
 * @returns {T}
 */
export function normalizeHeadings<T extends Node>(tree: T): T
export type Node = import('mdast').Root | import('mdast').Content
export type Heading = import('mdast').Heading
